msg.topic = `
CREATE TABLE IF NOT EXISTS tastiway_plans (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  start INTEGER NOT NULL,
  end INTEGER NOT NULL,
  orderId TEXT NOT NULL,
  productName TEXT NOT NULL,
  quantity INTEGER NOT NULL
);
`;

return msg;

var oldState = flow.get("oldState") || 0;
var newState = msg.payload;
var currentOrderId = flow.get("currentOrderId") || null;

var sqlMsg = null;

// Only count if an order is active
if (currentOrderId) {
    // Rising edge detection: 0 → 1
    if (oldState === 0 && newState === 1) {
        sqlMsg = {
            topic: `UPDATE current
                    SET counts = counts + 1
                    WHERE orderId = '${currentOrderId}';`
        };
        node.status({ fill: "green", shape: "dot", text: "Counting..." });
    }
} else {
    node.status({ fill: "red", shape: "ring", text: "Counting disabled" });
}

// Save the latest state
flow.set("oldState", newState);

// Only send if a count happened
return sqlMsg;

msg.topic=`
INSERT INTO count ( plan, count)
VALUES ('test',0);
`;

return msg;

if (!msg.error && msg.topic && msg.topic.toUpperCase().includes("INSERT INTO")) {
    flow.set("countingEnabled", true);
    node.status({ fill: "green", text: "Counting enabled" });
} else {
    flow.set("countingEnabled", false);
    node.status({ fill: "red", text: "Counting disabled" });
}
return msg;

var currentOrderId = flow.get("currentOrderId") || null;
var sqlMsg = null;

// if start is pressed
if (currentOrderId) {
        sqlMsg = {topic : `
            SELECT counts
            FROM current
            WHERE orderId = '${currentOrderId}';
        `};
        node.status({ fill: "green", shape: "dot", text: "Retrieving counts" }); 
} else {
    node.status({ fill: "red", shape: "ring", text: "Counting disabled" });
}

return sqlMsg;

if (msg.payload && msg.payload.length > 0) {
    const countValue = msg.payload[0].counts;

    node.status({ fill: "green", shape: "dot", text: "Count: " + countValue });

    // Send to UI
    msg.payload = countValue;
    return msg;
} else {
    node.status({ fill: "red", shape: "ring", text: "No data" });
    return null;
}

msg.topic = `

CREATE TABLE IF NOT EXISTS tastiway_process (
  orderId TEXT NOT NULL PRIMARY KEY,
  start INTEGER NOT NULL,
  stop INTEGER,
  counts INTEGER DEFAULT 0,
  uploaded INTEGER DEFAULT 0
);
`;

return msg;

msg.topic = `

CREATE TABLE IF NOT EXISTS current (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  orderId TEXT NOT NULL,
  counts INTEGER DEFAULT 0 
);
`;

return msg;

// Expecting msg.orderId to come from previous node
const orderId = msg.payload;

if (orderId) {
    flow.set("currentOrderId", orderId);
    node.status({ text: "Current Order: " + orderId });
} else {
    node.error("❌ Missing orderId argument!", msg);
}

return msg;

msg.topic=`
SELECT * FROM current
`;

return msg;

const orderId = flow.get("currentOrderId");

if (!orderId) {
    node.status({ fill: "red", shape: "ring", text: "❌ No currentOrderId found" });
    node.error("No currentOrderId set in flow context!");
    return null;
}

msg.payload = orderId;

// // Optionally, set msg.args if your Exec node uses an array of arguments
// msg.args = [orderId];

node.status({ text: `▶️ Sending ${orderId}` });

return msg;

// Trigger only when user opens the Production Plan page
if (
    msg.topic === "$pageview" &&
    msg.payload?.page?.path === "/production_plan"
) {
    // run your query or next logic
    msg.topic = "SELECT * FROM tastiway_plans;";
    return msg;
}

return null;

