.refresh-style button {
    font-weight: 600;
    color: white;
    background: linear-gradient(145deg, rgb(1, 39, 76), rgb(3, 64, 110));
    border: 0.1vw solid rgba(255, 255, 255, 0.2);
    border-radius: 14px;
    box-shadow:
        0 4px 10px rgba(1, 39, 76, 0.4),
        inset 0 0 12px rgba(255, 255, 255, 0.15);
    text-transform: none;
    letter-spacing: 0.5px;
    /* font-size: 2.3em; */
    /* font-size: calc(1.3vw + 0.6em); */
    font-size: calc(1vw + 0.5em);
    padding: 0.6em 1.2em;
    transition: all 0.25s ease;
    cursor: pointer;
    max-height: calc( 2 * var(--widget-row-height)); /* in case font size gets out of hand */
    overflow: hidden; /* in case text too wide at this font size */
}

/* hover + focus feedback */
.refresh-style button:hover,
.refresh-style button:focus {
    transform: scale(1.04);
    background: linear-gradient(145deg, rgb(3, 64, 110), rgb(1, 39, 76));
    box-shadow:
        0 6px 16px rgba(1, 39, 76, 0.5),
        inset 0 0 14px rgba(255, 255, 255, 0.2);
    border-color: rgba(255, 255, 255, 0.4);
}

/* optional active press effect */
.refresh-style button:active {
    transform: scale(0.98);
    box-shadow: inset 0 0 10px rgba(0, 0, 0, 0.4);
}

.finish-style button {
    font-weight: 600;
