msg.payload = {
    signal: msg.values.blueBtn ? "blue" : msg.values.yellowBtn ? "yellow" : null,
    message: msg.values.textMessage
};
return msg;

// Get previous active color
let activeColor = flow.get('activeColor') || null;
const pressedColor = msg.payload;

// Ignore if the same button is pressed
if (pressedColor === activeColor) {
    return null;
}

// Save the new active color
flow.set('activeColor', pressedColor);

// Construct button messages
const greenMsg = {
    ui_update: {
        buttonColor: pressedColor === 'green' ? 'green' : '#0a7d00'
    }
};

const blueMsg = {
    ui_update: {
        buttonColor: pressedColor === 'blue' ? 'blue' : '#003c8f'
    }
};

const redMsg = {
    ui_update: {
        buttonColor: pressedColor === 'red' ? 'red' : '#8b0000'
    }
};


// Output active color to downstream
const outputMsg = { payload: pressedColor };

return [ greenMsg,blueMsg, redMsg, outputMsg];

